hudson.tasks.Shell.command:
if [ "$CLUSTER_FROM_SCRATCH" = true ]; then
  # create the cluster
    # set required terraform variables to parsed out AWS creds and run terraform
    export TF_VAR_cluster_name=${CLUSTER_PREFIX}_aws
    export TF_VAR_aws_user_prefix=${CLUSTER_PREFIX}
    export TF_VAR_aws_access_key=${AWS_KEY_ID}
    export TF_VAR_aws_secret_key=${AWS_ACCESS_KEY}
    export TF_VAR_asg_wait_single=60
    export TF_VAR_asg_wait_total=${TOTAL_WAIT}

    # autotest cluster values
    export TF_VAR_node_count=$((NUMBER_OF_NODES-1))
    export TF_VAR_kubernetes_binaries_uri=${KUBERNETES_BINARIES_URI}
    export TF_VAR_aws_node_type=${NODE_TYPE}
    export TF_VAR_aws_etcd_type=${ETCD_TYPE}
    export TF_VAR_aws_master_type=${MASTER_TYPE}
    export TF_VAR_aws_apiserver_type=${API_SERVER_TYPE}
    export TF_VAR_aws_storage_type_etcd=ephemeral
    export TF_VAR_apiserver_count=${API_SERVER_COUNT}

  # start terraform
  terraform apply -input=false -state=${WORKSPACE}/terraform/aws/terraform.tfstate ${WORKSPACE}/terraform/aws
fi;

if [ "$RUN_ANSIBLE_DURING_SETUP" = true ]; then
  retries=10
    until ANSIBLE_SSH_PIPELINING=True ANSIBLE_SSH_RETRIES=100 ANSIBLE_HOST_KEY_CHECKING=False ansible-playbook -f ${ANSIBLE_FORKS} -i ${WORKSPACE}/terraform/aws/rendered/ansible.inventory ${WORKSPACE}/ansible/iaas_provision_local.yaml  --extra-vars "kubernetes_binaries_uri=${KUBERNETES_BINARIES_URI}" -v --diff
    do
      if [ $retries -eq 0 ]; then
        echo "Re ansible failed ..."
        exit 1
      fi

      retries=$((retries - 1))
      echo "Retrying ansible ..."
      sleep 10
    done
fi

if [ "$DESTROY_CLUSTER" = true ]; then export TF_VAR_cluster_name=${CLUSTER_PREFIX}_aws export TF_VAR_aws_user_prefix=${CLUSTER_PREFIX} export TF_VAR_aws_access_key=${AWS_KEY_ID} export TF_VAR_aws_secret_key=${AWS_ACCESS_KEY}
retries=10 until terraform destroy -input=false -force -state=${WORKSPACE}/terraform/aws/terraform.tfstate ${WORKSPACE}/terraform/aws do if [ $retries -eq 0 ]; then echo "Destroy failed ..." exit 1 fi
retries=$((retries - 1)) echo "Retrying destroy ..." sleep 10 done fi
