hudson.tasks.Shell.command:
# auth with vault and read credentials into a temp file
vault auth -method=github -address=https://vault.kubeme.io -tls-skip-verify token=${GITHUB_ORG_TOKEN}
vault read -address=https://vault.kubeme.io -tls-skip-verify -format=json aws/creds/root > temp.json

# set required terraform variables to parsed out AWS creds and run terraform
export TF_VAR_aws_user_prefix=pipeletconformance
export TF_VAR_aws_access_key=$(cat temp.json | jq -r '.data.access_key')
export TF_VAR_aws_secret_key=$(cat temp.json | jq -r '.data.secret_key')

# wait a bit and start terraform
sleep 30
terraform apply -input=false -state=${WORKSPACE}/terraform/aws/terraform.tfstate -var 'asg_wait_single=60' -var 'asg_wait_total=10' ${WORKSPACE}/terraform/aws

# copy the log file to S3
export AWS_ACCESS_KEY_ID=$(cat temp.json | jq -r '.data.access_key')
export AWS_SECRET_ACCESS_KEY=$(cat temp.json | jq -r '.data.secret_key')
aws s3 cp --acl public-read /opt/gobuild/kubernetes/kraken_${GIT_COMMIT}_conformance.log s3://kraken-e2e-logs/conformance

export TF_VAR_aws_user_prefix=pipeletconformance
export TF_VAR_aws_access_key=$(cat temp.json | jq -r '.data.access_key')
export TF_VAR_aws_secret_key=$(cat temp.json | jq -r '.data.secret_key')

# terraform destroy
terraform destroy -input=false -force -state=${WORKSPACE}/terraform/aws/terraform.tfstate ${WORKSPACE}/terraform/aws
